import React from "react"
import Movie from "./Movie"

class Movies extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
          error: null,
          isLoaded: false,
          items: []
      };
  }

  render() {
      const {error, isLoaded, items} = this.state;
      if(error){
          return <div>Ошибка: {error.message}</div>
      } else if(!isLoaded){
          return <div>Идет загрузка...</div>
      } else{
          return (
            console.log(items)
              // <React.Fragment>
