import React from "react";
import Movies from "../components/Movies"
import Search from "../components/Search"

class Main extends React.Component {
    state = {
        movies: [],
    }

    componentDidMount() {
        fetch("http://www.omdbapi.com/?s=matrix&apikey=36391301")
        .then(response => response.json())
        .then(data => this.setState({movies: data.Search}))
    }

    filmSearch = (data) => {
        fetch("http://www.omdbapi.com/?s="+String(data)+"&apikey=36391301")
        .then(response => response.json())
        .then(data => this.setState({movies: (data.Search !== false) ? data.Search : ["Title: no films"]}))
    }

    render() {
        const {movies} = this.state;
        return (
            <main className="container content">
                <Search filmSearch={this.filmSearch}/>
                {movies.length ? (<Movies movies={movies}/>) : (<div className="progress"><div className="indeterminate"></div>
  </div>)}
            </main>
        )
    }
}

export default Main;
