import React from "react";

class Filter extends React.Component{
    state = {
        filter: 'all',
    }

    selectFilter = (event) => {
        this.setState({filter: event.target.value})
        this.props.filmSearch(undefined,this.state.filter)
    }
    
    
    render() {
        const {filter} = this.state;
        return(
            <form>
                <input 
                    type="radio" 
                    name="filter"
                    value="all"
                    onChange={this.selectFilter} 
                /> Все
                <input 
                    type="radio" 
                    name="filter" 
                    value="movies" 
                    onChange={this.selectFilter}   
                /> Фильмы
                <input 
                    type="radio" 
                    name="filter"
                    value="series"
                    checked={filter === "series"}
                    onChange={this.selectFilter} 
                /> Сериалы
            </form>    
        )
    }
}

export default Filter;
