import React from "react";

class Filter extends React.Component{
    state = {
        filter: 'all',
    }

    selectFilter = (event) => {
        this.setState({filter: event.target.value})
        this.props.filmSearch(undefined,this.state.filter)
    }
    
    
    render() {
        const {filter} = this.state;
        return(
            <div className="radio-block">
                <input 
                    type="radio" 
                    name="filter"
                    value="all"
                    onChange={this.selectFilter} 
                    checked={filter === "all"}
                /> Сериалы
                <input 
                    type="radio" 
                    name="filter" 
                    value="movies" 
                    onChange={this.selectFilter} 
                    checked={filter === "movies"}  
                /> Фильмы
                <input 
                    type="radio" 
                    name="filter"
                    value="series"
                    checked={filter === "series"}
                    onChange={this.selectFilter} 
                /> Сериалы
                <label>
                    <input 
                    name="group1" 
                    type="radio" 
                    />
                    <span>Все</span>
                </label>
            </div>    
        )
    }
}

export default Filter;
