import React from "react";

class Filter extends React.Component{
    state = {
        filter: 'all',
    }

    selectFilter = (event) => {
        this.setState({filter: event.target.value})
        this.props.filmSearch(undefined,this.state.filter)
    }
    
    
    render() {
        const {filter} = this.state;
        return(
            <div className="radio-block">
                <label>
                    <input 
                        name="filter" 
                        type="radio"
                        value="all"
                        className="with-gap"
                        onChange={this.selectFilter} 
                        checked={filter === "all"} 
                    />
                    <span>Все</span>
                </label>
                <label>
                    <input 
                        name="filter" 
                        type="radio"
                        value="movies"
                        onChange={this.selectFilter} 
                        checked={filter === "movies"} 
                    />
                    <span>Фильмы</span>
                </label>
                <label>
                    <input 
                        name="filter" 
                        type="radio"
                        value="series"
                        onChange={this.selectFilter} 
                        checked={filter === "series"} 
                    />
                    <span>Сериалы</span>
                </label>
            </div>    
        )
    }
}

export default Filter;
