import React from "react";

class Search extends React.Component { 
    state = {
        name: '',
        filter: 'all',
    }

    keyEnter = (event) => {
        let type = "name"
        if(event.key === "Enter"){
            event.target.blur()
            this.props.filmSearch(type,this.state.name)
        }
    }

    render() {

        return (
            <div className="row">
                <div className="input-field col s12">
                    <input 
                        placeholder='Искать в библиотеке фильмов' 
                        id="email" 
                        type="email" 
                        className="validate"
                        value={this.state.name}
                        onChange={(e) => this.setState({name: e.target.value})}
                        onKeyDown={this.keyEnter}
                    />
                </div>
                <div className="radio-block">
                <label>
                    <input 
                        name="filter" 
                        type="radio"
                        value="all"
                        className="with-gap"
                        onChange={this.selectFilter} 
                        checked={filter === "all"} 
                    />
                    <span>Все</span>
                </label>
                <label>
                    <input 
                        name="filter" 
                        type="radio"
                        value="movies"
                        className="with-gap"
                        onChange={this.selectFilter} 
                        checked={filter === "movies"} 
                    />
                    <span>Фильмы</span>
                </label>
                <label>
                    <input 
                        name="filter" 
                        type="radio"
                        value="series"
                        className="with-gap"
                        onChange={this.selectFilter} 
                        checked={filter === "series"} 
                    />
                    <span>Сериалы</span>
                </label>
            </div>
            </div>
        )
        
    }
}

export default Search;
