import React from "react"
import Movie from "./Movie"

class Movies extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        };
    }

    componentDidMount() {
      const response = fetch(
        "https://jsonplaceholder.typicode.com/todos/"
      ).then((response) => response.json());
        fetch("http://www.omdbapi.com/?s=matrix&apikey=36391301&page=1-3")
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result.items
              });
            },
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }

    render() {
        const {error, isLoaded, items} = this.state;
        if(error){
            return <div>Ошибка: {error.message}</div>
        } else if(!isLoaded){
            return <div>Идет загрузка...</div>
        } else{
            return (
              console.log(items)
                // <React.Fragment>
                //     {items.map((item) => (
                //         <Movie title={item.Title} key={item.imdbID} year={item.Year} src={item.Poster}/>
                //     ))};
                // </React.Fragment>
            );
            
        }
    }
}


export default Movies;
